import React from 'react';
import styled  from 'styled-components';

const Imagevertical = styled.img`
    height: 500px;
    background-size: cover;
    width: 100%;
`


const Ol = styled.ul`
    
    counter-reset: li;
    list-style-type: none;
    margin-top: 16px;   

    ${Ol} > li::before {
    content: counter(li);
    counter-increment: li;
    
    
    padding: 3px 8px;
    width: 25px;
    height: 25px;
    margin-right: 8px;
    line-height: 35px;
    background: #1FB139;
    color: white;
    border-radius: 50%;
    text-align: center;
    vertical-align: middle;
    }

    ${Ol} > li {
        margin-bottom: 13px;
    }
`


const Hli = styled.span`
    font-size:17px;
    font-weight: 700;
    margin-bottom: 8px;
`

const Pli = styled.p`
    margin-bottom: 8px;
`

const Infobanner = () => {
    return ( 
        <>
        <div className="container">
            <div className="row my-5">
                <div className="col-12 col-md-6">
                <Imagevertical  className=" imagenUltimosBlog" />
                </div>
                <div className="col-12 col-md-6">
                <Ol>
                    <li><Hli>Elige un tema que te interese</Hli>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting
                    industry.</p></li>
                    <li><Hli>Documentate sobre ese tema</Hli>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting
                    industry.</p></li>
                    <li><Hli>Rededacta tu blog o noticia</Hli>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting
                    industry.</p></li>
                    <li><Hli>Súbela y podrás compartirla</Hli>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting
                    industry.</p></li>
                    <li><Hli>Obten votos y comentarios</Hli>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting
                    industry.</p></li>
                </Ol>
                </div>
            </div>
        </div>
        </>                
     );
}
 
export default Infobanner;
