    display: flex;
    justify-content: space-evenly;
    font-size: .9rem;
    text-align: center;
    color: #ffffff;
    background-color: #1FB139;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    
`


const Legend = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    padding: 10px;
    color: white;
`

const Valor = styled.div`
    font-weight: 600;
`

